<script>function first(node){ document.location="<?php echo teacher_site_url('notifications','pg='); ?>" + node;}function last(node){ document.location="<?php echo teacher_site_url('notifications','pg='); ?>" + node;}function next(node){ document.location="<?php echo teacher_site_url('notifications','pg='); ?>" + node;}function prev(node){ document.location="<?php echo teacher_site_url('notifications','pg='); ?>" + node;}</script>		<section id="main" class="column">				<h4 class="alert_info">Selamat Datang Di <?php echo get_school_name(); ?></h4>			<article class="module width_full">		<header><h3 style="width:200px;"> Pengumuman</h3>		<ul class="tabs">	   			<li><a href="#tab1">Pengumuman</a></li>	    			<li><a href="#tab2">Input</a></li>			</ul>		</header>		<div class="module_content">			<article class="stats_graph">							<header><h3 class="tabs_involved"><?php					if(!isset($currentNotif))$currentNotif=1;					if(!isset($totalNotif))$totalNotif=1;					if(!isset($val))$val=0;					$first=0;					if($val-1 > 0){						if($val == 1){						 $prev=$val-2;						}else{						 $prev=$val-2;;						}					}else{					 $prev = 0;					}					if($val-1 < $totalNotif-1)$next=$val;else $next=$totalNotif;					$last=$totalNotif-1;					$b_first=array('onclick'=>"first($first);",'style'=>'margin-top:0px;padding:3px;');					$b_prev=array('onclick'=>"prev($prev);",'style'=>'margin-top:0px;padding:3px;');					$b_next=array('onclick'=>"next($next);",'style'=>'margin-top:0px;padding:3px;');					$b_last=array('onclick'=>"last($last);",'style'=>'margin-top:0px;padding:3px;');					$input=array('name'=>'pg','style'=>'width:30px;text-align:center;','value'=>"$val"); 					echo form_open().form_button($b_first,'first');					echo form_button($b_prev,'<<');					echo form_input($input);					echo form_button($b_next,'>>');					echo form_button($b_last,'last');					echo "Pengumuman: ".$val.' dari: '.$totalNotif . form_close();				 ?></h3>					</header>		<div class="tab_container">			<div id="tab1" class="tab_content">				<table class="tablesorter" cellspacing="0"> 				<?php 				foreach($notif->result() as $r){				?>				<thead> 					<tr> 	   				 <th colspan=4><?php echo $r->post_title; //title ?></th> 					</tr> 				</thead> 				<tbody> 					<tr> 	    				 <td colspan="4"><div class="more"><?php echo substr($r->post_content,0,200).'...';//tgl ?></div></td> 					</tr>										<tr> 	    				 <td colspan="3"><?php echo $r->date_time;//tgl ?></td> 	    				 <td width="200px"><?php echo anchor('guru/teacher/view_current_notif/'.$r->ID_post,'<button class="more">Selengkapnya</button>');//button ?></td> 					</tr> 				<?php } ?>				</tbody> 				</table>				</div><!-- end of #tab1 -->					<div id="tab2" class="tab_content">				<table class="tablesorter" cellspacing="0"> 				<thead> 					<tr> 	   				<th colspan=3>Input Pengumuman</th> 					</tr> 				</thead> 				<tbody> 				<?php echo form_open(teacher_site_url('notifications','act=new')); ?>									<tr> 					<td>kategori</td> 	    				<td>:</td> 	    				<td><?php echo dropdown_categories('categories','',$model); ?></td> 					</tr> 									<tr> 					<td>Judul</td> 	    				<td>:</td> 	    				<td><?php echo text_input('title','Judul'); ?></td> 					</tr> 						<script src="<?php echo tinymce_url('tinymce.min.js');?>"></script>						<script>							 tinymce.init({selector:'textarea'});						</script>					<tr> 					<td vertical-align="top">Content</td> 	    				<td>:</td> 	    				<td><?php echo textarea('content','masukkan Text Isi'); ?></td> 					</tr> 										<tr> 					<td></td> 	    				<td></td> 	    				<td><?php echo form_submit('submit','Simpan'); ?></td> 					</tr> 									<?php echo form_close(); ?>				</tbody> 				</table>			</div><!-- end of #tab2 -->			    </div><!-- end of .tab_container -->			</article><!-- end of stats article -->					<div class="clear"></div>		</div>	</article><!--module width_full-->			</section></body></html>