/tr>				  <tr> <td>Alamat</td> <td>:</td> <td><?php echo $addr; ?></td> </tr>				  <tr> <td>Kota</td> <td>:</td> <td><?php echo $kota; ?></td> </tr>				  <tr> <td>Kode Pos</td> <td>:</td> <td><?php echo $post_code; ?></td> </tr>				  <tr> <td>No Telp</td> <td>:</td> <td><?php echo $notelp; ?></td> </tr>				  <tr> <td>Info Pribadi</td> <td>:</td> <td><?php echo $info; ?></td> </tr>				  <tr> 				  	<td></td>				  	<td></td>				  	<td><?php 				  	$l_pesan=teacher_site_url('send_message','id='.$username);				  	$l_nilai=teacher_site_url('viewScore','id='.$NIM);				  	$l_report=teacher_site_url('sendReport','id='.$username);				  	echo br().anchor($l_pesan,"<button>Kirim Pesan</button>");				  	echo anchor($l_nilai,"<button>Lihat Nilai</button>");				  	echo anchor($l_report,"<button>Laporkan Masalah</button>"); ?>				  	</td> </tr>				 </table>				</div><!-- end of #tab1 -->					<div id="tab2" class="tab_content">				 				 <table>				  <tr> <td>NIM</td> <td>:</td> <td><?php echo $NIM; ?></td> </tr>				  <tr> <td>Nama Ortu/Wali</td> <td>:</td> <td><?php echo $p_nama; ?></td> </tr>				  <tr> <td>Agama</td> <td>:</td> <td><?php echo $p_agama; ?></td> </tr>				  <tr> <td>Jenis Kelamin</td> <td>:</td> <td><?php echo $p_jk; ?></td> </tr>				  <tr> <td>Alamat</td> <td>:</td> <td><?php echo $p_addr; ?></td> </tr>				  <tr> <td>No Telp</td> <td>:</td> <td><?php echo $p_number; ?></td> </tr>				 </table>				</div><!-- end of #tab2 -->			</div><!-- end of .tab_container -->			</article><!-- end of stats article -->					<div class="clear"></div>		</div>	</article><!--module width_full-->			</section></body></html>