<?phpforeach($data->result() as $r){	$id=$r->ID_post;	$category=$r->post_categories;	$title=$r->post_title;	$content=$r->post_content;	$st=$r->activated;}?>	<section id="main" class="column">				<h4 class="alert_info">Selamat Datang Di <?php echo get_school_name(); ?></h4>			<article class="module width_full">		<header><h3 style="width:200px;"> Pengumuman</h3>		<ul class="tabs">	   			<li><a href="#tab1">Edit</a></li>			</ul>		</header>		<div class="module_content">			<article class="stats_graph">					<div class="tab_container">				<div id="tab1" class="tab_content">				<table class="tablesorter" cellspacing="0"> 				<thead> 					<tr> 	   				<th colspan=3>Input Pengumuman</th> 					</tr> 				</thead> 				<tbody> 				<?php echo form_open(admin_site_url('edit_post','act=update&id='.$id)); ?>									<tr> 					<td>Kategori</td> 	    				<td>:</td> 	    				<td><?php echo dropdown_categories('categories',$category,$model); ?></td> 					</tr> 									<tr> 					<td>Judul</td> 	    				<td>:</td> 	    				<td><?php echo text_input('title',$title); ?></td> 					</tr> 						<script src="<?php echo tinymce_url('tinymce.min.js');?>"></script>						<script>							 tinymce.init({selector:'textarea'});						</script>					<tr> 					<td vertical-align="top">Content</td> 	    				<td>:</td> 	    				<td><?php echo textarea('content',$content); ?></td> 					</tr> 										<tr> 					<td>Status Post</td> 	    				<td>:</td> 	    				<td><?php 	    				$arr=array("0"=>'not active',"1"=>'active');	    				echo dropdown('state',$arr,$st); ?></td> 					</tr> 										<tr> 					<td></td> 	    				<td></td> 	    				<td><?php echo form_submit('submit','Simpan'); ?></td> 					</tr> 									<?php echo form_close(); ?>				</tbody> 				</table>			</div><!-- end of #tab1 -->			    </div><!-- end of .tab_container -->			</article><!-- end of stats article -->			<div class="clear"></div>		</div>	 </article><!--module width_full-->	</section></body></html>